CREATE TABLE IF NOT EXISTS suppliers (id_supplier SERIAL PRIMARY KEY, company_name VARCHAR(60), contact_name VARCHAR(30), contact_job_title VARCHAR(30), address_country VARCHAR(30), address_state VARCHAR(30), address_index VARCHAR(10), address_city VARCHAR(30), address_local VARCHAR(100), phone VARCHAR(20), fax VARCHAR(20), homepage VARCHAR(100));
